{
  "level": "Error",
  "message": "Failed to process order: Database connection timeout",
  "jobId": "{{jobId}}",
  "serverName": "{{serverName}}",
  "category": "OrderProcessing",
  "exception": {
    "type": "System.TimeoutException",
    "message": "The operation has timed out.",
    "stackTrace": "   at OrderService.ProcessOrder() in OrderService.cs:line 45\n   at OrderController.Submit() in OrderController.cs:line 23",
    "source": "OrderService",
    "innerException": {
      "type": "SqlClient.SqlException",
      "message": "Connection timeout expired"
    }
  },
  "properties": {
    "OrderId": "ORD-12345",
    "CustomerId": "CUST-001"
  },
  "tags": ["error", "database", "timeout"]
}

{
  "logs": [
    {
      "level": "Information",
      "message": "Job started",
      "jobId": "{{jobId}}",
      "serverName": "{{serverName}}"
    },
    {
      "level": "Debug",
      "message": "Processing step 1 of 5",
      "jobId": "{{jobId}}",
      "serverName": "{{serverName}}"
    },
    {
      "level": "Warning",
      "message": "Slow query detected: 2500ms",
      "jobId": "{{jobId}}",
      "serverName": "{{serverName}}",
      "properties": {
        "QueryTime": 2500,
        "Query": "SELECT * FROM Orders"
      }
    },
    {
      "level": "Information",
      "message": "Job completed successfully",
      "jobId": "{{jobId}}",
      "serverName": "{{serverName}}"
    }
  ]
}

{
  "serverName": "APP-SERVER-01",
  "displayName": "Application Server 01",
  "description": "Primary application server for production workloads",
  "ipAddress": "192.168.1.10",
  "operatingSystem": "Windows Server 2022",
  "agentVersion": "1.0.0",
  "tags": ["production", "primary"],
  "metadata": {
    "datacenter": "DC1",
    "rack": "R01"
  }
}

{
  "name": "Job Failure Alert",
  "description": "Triggers when a job fails more than 3 times consecutively",
  "alertType": "JobFailure",
  "severity": "High",
  "isActive": true,
  "jobId": "{{jobId}}",
  "conditions": {
    "consecutiveFailures": 3,
    "timeWindowMinutes": 60
  },
  "throttleMinutes": 30,
  "notificationChannels": ["email", "slack"],
  "recipients": ["ops-team@example.com"],
  "metadata": {
    "escalation": "on-call"
  }
}

{
  "resolution": "Fixed the database connection issue",
  "note": "Root cause: connection pool exhaustion"
}

